CREATE OR REPLACE PROCEDURE limpar_impressao_hor_prescr (nr_prescricao_p bigint, nr_seq_turno_p bigint, cd_local_estoque_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_itens_turno_w	bigint;


BEGIN


select	count(*)
into STRICT	qt_itens_turno_w
from 	prescr_material a,
	prescr_mat_hor b
where	a.nr_prescricao		= b.nr_prescricao
and	a.nr_sequencia		= b.nr_seq_material
and	a.nr_prescricao		= nr_prescricao_p
and	a.cd_local_estoque	= cd_local_estoque_p
and	b.nr_seq_turno		= nr_seq_turno_p
and	Obter_se_horario_liberado(b.dt_lib_horario, b.dt_horario) = 'S';

if (qt_itens_turno_w > 0) then

	update	prescr_mat_hor a
	set	dt_emissao_farmacia	 = NULL,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_prescricao		= nr_prescricao_p
	and	nr_seq_turno		= nr_seq_turno_p
	and	exists (SELECT	1
		from	prescr_material x
		where	x.nr_prescricao		= a.nr_prescricao
		and	x.nr_sequencia		= a.nr_seq_material
		and	x.cd_local_estoque	= cd_local_estoque_p);

	update	prescr_medica
	set	dt_emissao_farmacia	 = NULL,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_prescricao		= nr_prescricao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
