CREATE OR REPLACE PROCEDURE limpar_itens_carga_serv_tuss ( nr_seq_carga_tuss_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
ind_w		bigint;
dt_liberacao_w	timestamp;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	tuss_servico_item
	where	nr_seq_carga_tuss = nr_seq_carga_tuss_p
	order by 1;


BEGIN

ind_w:= 0;

select 	max(dt_liberacao)
into STRICT	dt_liberacao_w
from 	tuss_servico
where 	nr_sequencia = nr_seq_carga_tuss_p;

if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
	-- Não é possível excluir itens de uma carga tuss já liberada!
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(244309);
end if;

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	begin
	delete from tuss_servico_item
	where	nr_sequencia = nr_sequencia_w;
	exception
		when others then
		ind_w:= ind_w;
	end;

	ind_w:= ind_w + 1;

	if (ind_w > 100) then
		ind_w:= 0;
		commit;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
