CREATE OR REPLACE PROCEDURE limpar_marcacao_ageint ( nr_seq_ageint_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_tipo_agenda_w	bigint;
cd_agenda_w		bigint;
nr_seq_agenda_w		bigint;
nr_sequencia_w		bigint;
nr_seq_turno_w		bigint;
nr_minuto_duracao_marc_w 	bigint;

C01 CURSOR FOR
	SELECT	cd_agenda,
		nr_Seq_agenda,
		nr_Sequencia,
		nr_minuto_duracao
	from	ageint_marcacao_usuario
	where	nm_usuario	 	= nm_usuario_p
	and	coalesce(ie_Gerado,'N')	<> 'S'
	and	nr_seq_ageint		= nr_seq_ageint_p;


BEGIN
open C01;
loop
fetch C01 into
	cd_Agenda_w,
	nr_seq_agenda_w,
	nr_sequencia_w,
	nr_minuto_duracao_marc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	cd_tipo_agenda
	into STRICT	cd_tipo_agenda_w
	from	agenda
	where	cd_agenda	= cd_agenda_w;

	if (cd_tipo_agenda_w	= 2) then
		CALL liberar_horario_agenda_exame(nr_seq_Agenda_w, nm_usuario_p);

		CALL liberar_hor_dur_age_exame(nr_seq_Agenda_w, nr_minuto_duracao_marc_w, nm_usuario_p);

		/*update	agenda_paciente
		set	ie_status_agenda	= 'L',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= sysdate
		where	nr_sequencia		= nr_seq_agenda_w;*/
	else
		select	max(nr_seq_turno)
		into STRICT	nr_seq_turno_w
		from	agenda_consulta
		where	nr_sequencia	= nr_seq_agenda_w;
		liberar_horario_agecons(nr_seq_agenda_w, nm_usuario_p, nr_seq_turno_w);
		/*update	agenda_consulta
		set	ie_status_agenda	= 'L',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= sysdate
		where	nr_sequencia		= nr_seq_agenda_w;*/
	end if;

	end;
end loop;
close C01;

delete	FROM ageint_encaixe_usuario
where	nm_usuario	= nm_usuario_p
and	nr_seq_ageint	= nr_seq_ageint_p;

delete	FROM ageint_horarios_usuario
where	nm_usuario		= nm_usuario_p;

delete 	FROM ageint_marcacao_usuario
where	nm_usuario		= nm_usuario_p
and	coalesce(ie_Gerado,'N')	<> 'S'
and	nr_seq_ageint		= nr_seq_ageint_p;

delete	FROM ageint_lib_usuario
where	nm_usuario		= nm_usuario_p
and	nr_seq_ageint		= nr_seq_ageint_p;

delete 	FROM ageint_sugestao_horarios
where	nm_usuario	= nm_usuario_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
