CREATE OR REPLACE PROCEDURE limpar_orgao_envio_cobr_selec ( ie_tipo_p text, nm_usuario_p text) AS $body$
DECLARE


/*
IE_TIPO_P:
T = Titulos
C = Cheques
O = Outros
X = Todos os tipos
*/
BEGIN

if (ie_tipo_p = 'T') or (ie_tipo_p = 'X') then

	update	titulo_receber_orgao_cobr
	set	ie_selecionado	= 'N'
	where	ie_selecionado	= 'S';

end if;

if (ie_tipo_p = 'C') or (ie_tipo_p = 'X') then

	update	cheque_cr_orgao_cobr
	set	ie_selecionado	= 'N'
	where	ie_selecionado	= 'S';

end if;

if (ie_tipo_p = 'O') or (ie_tipo_p = 'X') then

	update	outros_orgao_cobr
	set	ie_selecionado	= 'N'
	where	ie_selecionado	= 'S';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
