CREATE OR REPLACE PROCEDURE limpar_regras_acomod_pacote ( ds_lista_pacotes_p text, cd_convenio_p bigint) AS $body$
DECLARE


nr_seq_pacote_w			bigint;

c03 CURSOR FOR
	SELECT	nr_seq_pacote
	from	pacote
	where	obter_se_contido(nr_seq_pacote, elimina_aspas(ds_lista_pacotes_p)) = 'S'
	and	cd_convenio = cd_convenio_p
	and 	ie_situacao = 'A';


BEGIN


open	c03;
loop
fetch	c03 into
	nr_seq_pacote_w;
EXIT WHEN NOT FOUND; /* apply on c03 */
	begin

	delete 	from pacote_material
	where 	nr_seq_pacote 	 = nr_seq_pacote_w;

	delete from pacote_procedimento
	where 	nr_seq_pacote = nr_seq_pacote_w;

	delete from pacote_tipo_acomodacao
	where nr_seq_pacote = nr_seq_pacote_w;

	end;
end loop;
close c03;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
