CREATE OR REPLACE PROCEDURE limpar_ultima_etapa_finalizada ( nr_interno_conta_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_ant_w		bigint :=	0;
ie_existe_w		varchar(1);

c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	fatur_etapa b,
		conta_paciente_etapa a
	where	a.nr_seq_etapa = b.nr_sequencia
	and	a.nr_interno_conta = nr_interno_conta_p
	and	coalesce(b.ie_situacao,'A') = 'A'
	and	(a.dt_fim_etapa IS NOT NULL AND a.dt_fim_etapa::text <> '')
	and	a.nr_sequencia <> nr_sequencia_p
	order by a.dt_fim_etapa;


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (nr_seq_ant_w <> nr_sequencia_w) then
		nr_seq_ant_w := nr_sequencia_w;
	end if;
	end;
end loop;
close c01;

if (nr_seq_ant_w <> 0) then
	update	conta_paciente_etapa
	set	dt_fim_etapa  = NULL
	where	nr_sequencia = nr_seq_ant_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
