SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE link_procedure_to_drg ( nr_sequencia_p bigint, nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_drg_w	procedimento_paciente.nr_sequencia%type;
nr_seq_dado_w 		procedimento_paciente_dado.nr_sequencia%type;

qt_reg_dados_w 		bigint;


BEGIN

select	max(b.nr_sequencia) 
into STRICT 	nr_sequencia_drg_w 
from 	conta_paciente a, 
		procedimento_paciente b
where  	a.nr_interno_conta =  nr_interno_conta_p 
and		a.nr_interno_conta = b.nr_interno_conta
and		coalesce(b.cd_motivo_exc_conta::text, '') = ''
and 	b.ie_origem_proced = 15;

select	count(*)
into STRICT 	qt_reg_dados_w
from 	procedimento_paciente_dado
where 	nr_seq_proc  = nr_sequencia_p;

  if (nr_sequencia_drg_w = nr_sequencia_p) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1073050);
  end if;

  if (coalesce(nr_sequencia_drg_w::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1085069);
  end if;

  if (qt_reg_dados_w = 0) then
    select	nextval('procedimento_paciente_dado_seq') 
	into STRICT 	nr_seq_dado_w 
	;
		
    insert
    into procedimento_paciente_dado(
        nr_sequencia,
        nr_seq_proc,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        dt_atualizacao,
        nm_usuario,
        nr_seq_proc_drg
       )
       values (
		nr_seq_dado_w,
        nr_sequencia_p,
        clock_timestamp(),
        nm_usuario_p,
        clock_timestamp(),
        nm_usuario_p,
        nr_sequencia_drg_w
        );
  else
	
    update	procedimento_paciente_dado
    set 	nr_seq_proc_drg = nr_sequencia_drg_w
    where 	nr_seq_proc = nr_sequencia_p;
		
  end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
