SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE link_proc_cancelacct ( nr_interno_new_p bigint, nr_interno_old_p bigint, nm_usuario_p text) AS $body$
DECLARE

							
nr_seq_proc_drg_w	procedimento_paciente_dado.nr_seq_proc_drg%type;
cd_procedimento_drg_w	procedimento_paciente.cd_procedimento%type;
ie_origem_proced_drg_w	procedimento_paciente.ie_origem_proced%type;
cd_procedimento_w	procedimento_paciente.cd_procedimento%type;
ie_origem_proced_w	procedimento_paciente.ie_origem_proced%type;
nr_sequencia_old_w	procedimento_paciente.nr_sequencia%type;
nr_sequencia_w		procedimento_paciente.nr_sequencia%type;
qt_register		bigint;
nr_seq_dado_w		procedimento_paciente_dado.nr_sequencia%type;
nr_sequencia_new_p 	procedimento_paciente.nr_sequencia%type;
nr_seq_proc_w		procedimento_paciente_dado.nr_seq_proc%type;

c01 CURSOR FOR
SELECT 	a.nr_sequencia
from	procedimento_paciente a
where	a.nr_interno_conta = nr_interno_old_p
and	exists (select	1
		from	procedimento_paciente_dado x
		where	a.nr_sequencia = x.nr_seq_proc);
							

BEGIN

begin

open c01;
loop
fetch C01 into
	nr_sequencia_old_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	--- Getting the sequence of the DRG into the PROCEDIMENTO_PACIENTE
	select	coalesce(max(nr_seq_proc_drg),0),
		coalesce(max(nr_sequencia),0),
		coalesce(max(nr_seq_proc),0)
	into STRICT	nr_seq_proc_drg_w,
		nr_seq_dado_w,
		nr_seq_proc_w
	from	procedimento_paciente_dado
	where	nr_seq_proc = nr_sequencia_old_w;
	
	if (nr_seq_dado_w >0) then
		if (nr_seq_proc_drg_w > 0) then
			begin

			-- Getting the DRG code from procedure function from the old account
			select 	max(cd_procedimento),
				max(ie_origem_proced)
			into STRICT	cd_procedimento_drg_w,
				ie_origem_proced_drg_w
			from	procedimento_paciente
			where	nr_sequencia = nr_seq_proc_drg_w;
			
			select 	max(cd_procedimento),
				max(ie_origem_proced)
			into STRICT	cd_procedimento_w,
				ie_origem_proced_w
			from	procedimento_paciente
			where	nr_sequencia = nr_seq_proc_w;
			
			select 	max(nr_sequencia)
			into STRICT	nr_sequencia_new_p
			from    procedimento_paciente
			where	cd_procedimento = cd_procedimento_w
			and 	ie_origem_proced = ie_origem_proced_w
			and 	nr_interno_conta = nr_interno_new_p;

			-- Getting the new sequence of the DRG code from the new account
			select 	max(nr_sequencia)
			into STRICT	nr_sequencia_w
			from    procedimento_paciente
			where	cd_procedimento = cd_procedimento_drg_w
			and 	ie_origem_proced = ie_origem_proced_drg_w
			and 	nr_interno_conta = nr_interno_new_p;
	
			insert into procedimento_paciente_dado( nr_sequencia,
								nr_seq_proc,
								dt_atualizacao_nrec,
								nm_usuario_nrec,
								dt_atualizacao,
								nm_usuario,
								nr_seq_proc_drg)
							values ( nextval('procedimento_paciente_dado_seq'),
								nr_sequencia_new_p,
								clock_timestamp(),
								nm_usuario_p,
								clock_timestamp(),
								nm_usuario_p,
								nr_sequencia_w);


			end;
	
		else
			
			select 	max(cd_procedimento),
				max(ie_origem_proced)
			into STRICT	cd_procedimento_w,
				ie_origem_proced_w
			from	procedimento_paciente
			where	nr_sequencia = nr_seq_proc_w;
			
			select 	max(nr_sequencia)
			into STRICT	nr_sequencia_new_p
			from    procedimento_paciente
			where	cd_procedimento = cd_procedimento_w
			and 	ie_origem_proced = ie_origem_proced_w
			and 	nr_interno_conta = nr_interno_new_p;
			
			insert into procedimento_paciente_dado( nr_sequencia,
								nr_seq_proc,
								dt_atualizacao_nrec,
								nm_usuario_nrec,
								dt_atualizacao,
								nm_usuario,
								nr_seq_proc_drg)
							values ( nextval('procedimento_paciente_dado_seq'),
								nr_sequencia_new_p,
								clock_timestamp(),
								nm_usuario_p,
								clock_timestamp(),
								nm_usuario_p,
								null);
		end if;
	end if;
	
	
	end;
end loop;
close c01;
exception when others then
	 CALL gravar_log_tasy(9001,	substr('LINK_PROC_CANCELACCT - Exception Erro:'|| substr(to_char(sqlerrm),1,2000),1,2000)
				||' nr_interno_new_p'	|| nr_interno_new_p
				||' nr_interno_old_p ' 	|| nr_interno_old_p
				||' nm_usuario_p:'	|| USER, USER);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE link_proc_cancelacct ( nr_interno_new_p bigint, nr_interno_old_p bigint, nm_usuario_p text) FROM PUBLIC;

