CREATE OR REPLACE PROCEDURE liquidar_titulo_receber_html5 ( nm_usuario_p text, nr_titulo_p bigint, dt_liquidacao_p timestamp) AS $body$
DECLARE


vl_saldo_titulo_w	titulo_receber.vl_saldo_titulo%type;


BEGIN

select	coalesce(max(vl_saldo_titulo),0)
into STRICT	vl_saldo_titulo_w
from	titulo_receber
where	nr_titulo		= nr_titulo_p;

if (vl_saldo_titulo_w <> 0) then
	CALL wheb_mensagem_pck.EXIBIR_MENSAGEM_ABORT(174075);
end if;


CALL LIQUIDAR_TITULO_RECEBER(nm_usuario_p, nr_titulo_p, dt_liquidacao_p);

end;
$body$
LANGUAGE PLPGSQL
