CREATE OR REPLACE PROCEDURE load_data_protocol_event_type ( nm_usuario_p usuario.nm_usuario%type ) AS $body$
DECLARE


C01 CURSOR FOR
        SELECT  nextval('tipo_evento_protocolo_seq') as nr_sequencia,
                vl_dominio as cd_dominio_tp_evento,
                null as nr_ordem,
                clock_timestamp() as dt_atualizacao,
                nm_usuario_p as nm_usuario,
                clock_timestamp() as dt_atualizacao_nrec,
                nm_usuario_p as nm_usuario_nrec
        from    valor_dominio vd
        where   cd_dominio = 8828
        and     not exists ( SELECT  1
                            from    tipo_evento_protocolo tep
                            where   tep.cd_dominio_tp_evento = vd.vl_dominio);

C01row C01%rowtype;


BEGIN
	-- Ler valores dominios e adicionar o que falta na tabela.	
	for C01row in C01
	loop
		insert into tipo_evento_protocolo(
				nr_sequencia,
				cd_dominio_tp_evento,
				nr_ordem,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
		values (C01row.nr_sequencia,
				C01row.cd_dominio_tp_evento,
				C01row.nr_ordem,
				C01row.dt_atualizacao,
				C01row.nm_usuario,
				C01row.dt_atualizacao_nrec,
				C01row.nm_usuario_nrec);
	end loop;

	-- Remove vl_dominio que foi excluido.
	delete from tipo_evento_protocolo
	where  nr_sequencia in (	SELECT  tep.nr_sequencia
								from    tipo_evento_protocolo tep
								where   not exists ( select  1
													from    valor_dominio vd
													where   vd.cd_dominio = 8828
													and     vd.vl_dominio = tep.cd_dominio_tp_evento ) );

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		commit;
	end if;						

end;
$body$
