SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE localiza_expressao_invalidas () AS $body$
DECLARE


qt_registro_w	bigint;
cd_expressao_w	bigint;

C01 CURSOR FOR
SELECT 	cd_expressao
from	dic_expressao;


BEGIN

delete from w_dic_expressao;

open C01;
loop
fetch C01 into
	cd_expressao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_registro_w:= 0;

	CALL utilizacao_expressao(cd_expressao_w,'bcgucowski');

	select	count(*)
	into STRICT	qt_registro_w
	from	w_local_expressao
	where	nm_usuario = 'bcgucowski';

	if qt_registro_w = 0 then
		insert into w_dic_expressao(cd_expressao) values (cd_expressao_w);
	end if;


	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
