CREATE OR REPLACE PROCEDURE log_impressao_auto ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, dt_atualizacao_p timestamp, nm_usuario_p text, dt_atualizacao_nrec_p timestamp, nm_usuario_nrec_p text, cd_perfil_p bigint, cd_relatorio_p bigint, dt_impressao_p timestamp, dt_prox_impressao_p timestamp, ds_param_impressao_p text, ds_titulo_relat_p text) AS $body$
DECLARE


dt_prox_impressao_w timestamp;
nr_sequencia_log_w bigint;

BEGIN

CALL atualizar_emissao_relat_auto(nr_sequencia_p, clock_timestamp(), nm_usuario_nrec_p);

select max(dt_proxima_emissao)
into STRICT dt_prox_impressao_w
from relatorio_auto_imp
where nr_sequencia = nr_sequencia_p;

select coalesce(max(nr_sequencia) + 1, 1)
into STRICT nr_sequencia_log_w
from log_impressao_automatica;

insert into log_impressao_automatica(
    nr_sequencia,
    cd_estabelecimento,
    dt_atualizacao,
    nm_usuario,
    dt_atualizacao_nrec,
    nm_usuario_nrec,
    cd_perfil,
    cd_relatorio,
    dt_impressao,
    dt_prox_impressao,
    ds_param_impressao,
    ds_titulo_relat)
values (
    nr_sequencia_log_w,
    cd_estabelecimento_p,
    clock_timestamp(),
    nm_usuario_p,
    clock_timestamp(),
    nm_usuario_nrec_p,
    cd_perfil_p,
    cd_relatorio_p,
    clock_timestamp(),
    dt_prox_impressao_w,
    ds_param_impressao_p,
    ds_titulo_relat_p);

end;
$body$
