	and        ((coalesce(cd_video_unidade_atend::text, '') = '') or (cd_video_unidade_atend = cd_video_und_atend_p))
	and        ((coalesce(nr_atendimento::text, '') = '') or (nr_atendimento = nr_atendimento_p));

