CREATE OR REPLACE PROCEDURE lote_entrada_reconv_urg ( nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_prescr_w	bigint;
nr_seq_lote_entrada_w	bigint;
nr_seq_ficha_w			bigint;
cd_pessoa_fisica_w		varchar(10);
nr_seq_exame_w			bigint;
nr_seq_reconvocado_w	bigint;
cd_material_exame_w		varchar(10);

C01 CURSOR FOR
	SELECT	nr_sequencia,
			nr_seq_exame,
			cd_material_exame
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and		(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '');


BEGIN

select	coalesce(max(nr_seq_lote_entrada),0), coalesce(max(nr_seq_ficha_lote),0), max(cd_pessoa_fisica)
into STRICT	nr_seq_lote_entrada_w, nr_seq_ficha_w, cd_pessoa_fisica_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

if (nr_seq_ficha_w > 0) then

	select 	coalesce(nr_sequencia,0)
	into STRICT	nr_seq_reconvocado_w
	from	lote_ent_reconvocado
	where	nr_seq_ficha_lote = nr_seq_ficha_w;

	if (coalesce(nr_seq_reconvocado_w,0) = 0) then

		select	nextval('lote_ent_reconvocado_seq')
		into STRICT	nr_seq_reconvocado_w
		;

		insert into lote_ent_reconvocado(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_fisica,
			nr_seq_lote_sec,
			nr_seq_ficha_lote)
		values (nr_seq_reconvocado_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_w,
			nr_seq_lote_entrada_w,
			nr_seq_ficha_w);

	end if;

	open C01;
	loop
	fetch C01 into
		nr_seq_proc_prescr_w,
		nr_seq_exame_w,
		cd_material_exame_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		insert	into LOTE_ENT_RECONVOCADO_ITEM(
			NR_SEQUENCIA,
			NR_SEQ_EXAME,
			DT_ATUALIZACAO,
			NM_USUARIO,
			CD_MATERIAL_EXAME,
			NR_SEQ_RECONVOCADO,
			DT_ATUALIZACAO_NREC,
			NM_USUARIO_NREC,
			IE_TIPO_BUSCA
		) values (
			nextval('lote_ent_reconvocado_item_seq'),
			nr_seq_exame_w,
			clock_timestamp(),
			nm_usuario_p,
			cd_material_exame_w,
			nr_seq_reconvocado_w,
			clock_timestamp(),
			nm_usuario_p,
			'A'
		);

		end;
	end loop;
	close C01;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
