CREATE OR REPLACE PROCEDURE lote_ent_altera_locais ( nr_seq_consulta_p bigint, nr_seq_tratamento_p bigint, nr_seq_reconvocado_p bigint, ie_opcao_p bigint) AS $body$
DECLARE


/*
IE_OPCAO_P
1 - Local de consulta
2 - Local de tratamento
*/
BEGIN

if 	((coalesce(nr_seq_consulta_p,0) <> 0) or (coalesce(nr_seq_tratamento_p,0) <> 0)) and (nr_seq_reconvocado_p IS NOT NULL AND nr_seq_reconvocado_p::text <> '') then

	if (ie_opcao_p = 1) then

		update	lote_ent_reconvocado_cont
		set		NR_SEQ_LOCAL_CONS = nr_seq_consulta_p
		where	NR_SEQ_RECONVOCADO = nr_seq_reconvocado_p;

		update	lote_ent_reconvocado
		set		NR_SEQ_LOCAL_CONS = nr_seq_consulta_p
		where	NR_SEQUENCIA = nr_seq_reconvocado_p;

	elsif (ie_opcao_p = 2) then

		update	lote_ent_reconvocado_cont
		set		NR_SEQ_LOCAL_TRAT = nr_seq_tratamento_p
		where	NR_SEQ_RECONVOCADO = nr_seq_reconvocado_p;

		update	lote_ent_reconvocado
		set		NR_SEQ_LOCAL_TRAT = nr_seq_tratamento_p
		where	NR_SEQUENCIA = nr_seq_reconvocado_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
