CREATE OR REPLACE PROCEDURE lote_ent_baixar_ficha (nr_atendimento_p bigint, nr_prescricao_p bigint, nm_usuario_p text, nr_seq_motivo_p bigint, cd_pessoa_fisica_p text, ds_observacao_p text, nr_seq_ficha_p bigint) AS $body$
DECLARE

 
ds_motivo_susp_w	varchar(255);
					

BEGIN 
 
SELECT 	max(ds_motivo) 
into STRICT	ds_motivo_susp_w 
FROM  	motivo_suspensao_prescr 
WHERE 	1=1 
AND	  	ie_situacao = 'A' 
and		nr_sequencia = nr_seq_motivo_p;
 
CALL suspender_prescricao(nr_prescricao_p, nr_seq_motivo_p, ds_motivo_susp_w, nm_usuario_p, 'N');
 
update	lote_ent_sec_ficha 
set		ie_suspenso = 'S' 
where	nr_sequencia = nr_seq_ficha_p;
--cancelar_atendimento_paciente(nr_atendimento_p, nm_usuario_p, nr_seq_motivo_p, cd_pessoa_fisica_p, ds_observacao_p); 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
