CREATE OR REPLACE PROCEDURE lote_ent_exibir_result_laudo ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) AS $body$
DECLARE


ie_exibe_result_w	varchar(1);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

	select	max(IE_LOTE_ENT_EXIBE_RESULT)
	into STRICT	ie_exibe_result_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_prescr_p;

	if (coalesce(ie_exibe_result_w,'S') = 'S') then

		update	prescr_procedimento
		set	ie_lote_ent_exibe_result = 'N'
		where	nr_sequencia = nr_seq_prescr_p
		and	nr_prescricao = nr_prescricao_p;

	elsif (ie_exibe_result_w = 'N') then

		update	prescr_procedimento
		set	ie_lote_ent_exibe_result = 'S'
		where	nr_sequencia = nr_seq_prescr_p
		and	nr_prescricao = nr_prescricao_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
