CREATE OR REPLACE PROCEDURE lote_ent_transfere_ficha_lote (nr_seq_ficha_p bigint, nr_seq_lote_antigo_p bigint, nr_seq_lote_novo_p bigint, nr_prescricao_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_ficha_p IS NOT NULL AND nr_seq_ficha_p::text <> '') and (nr_seq_lote_antigo_p IS NOT NULL AND nr_seq_lote_antigo_p::text <> '') and (nr_seq_lote_novo_p IS NOT NULL AND nr_seq_lote_novo_p::text <> '') then

	update	lote_ent_sec_ficha
	set	nr_seq_lote_sec = nr_seq_lote_novo_p
	where	nr_sequencia = nr_seq_ficha_p
	and	nr_seq_lote_sec = nr_seq_lote_antigo_p;

