CREATE OR REPLACE PROCEDURE lote_susp_reconvocacao (nr_seq_ficha_p bigint, nm_usuario_p text) AS $body$
DECLARE




nr_seq_ficha_w		lote_ent_sec_ficha.nr_sequencia%type;
nr_seq_reconvocado_w	lote_ent_reconvocado.nr_sequencia%type;


C01 CURSOR FOR
	SELECT	b.nr_sequencia, c.nr_sequencia nr_seq_reconvocado
	from	lote_ent_secretaria a,
		lote_ent_sec_ficha  b,
		lote_ent_reconvocado c
	where	1 = 1
	and	a.nr_sequencia  	= b.nr_seq_lote_sec
	and	c.nr_seq_ficha_lote  	= b.nr_sequencia
	and	c.nr_seq_lote_sec  	= a.nr_sequencia
	and	b.nr_sequencia  	= nr_seq_ficha_p
	order by 1;

c01_w	C01%rowtype;


BEGIN

if (nr_seq_ficha_p IS NOT NULL AND nr_seq_ficha_p::text <> '') then

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update  lote_ent_reconvocado
	set		nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp(),
			dt_suspensao_rec = clock_timestamp()
	where	nr_sequencia = c01_w.nr_seq_reconvocado
	and		nr_seq_ficha_lote = c01_w.nr_sequencia;

	end;
end loop;
close C01;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
