CREATE OR REPLACE PROCEDURE man_abonar_sla ( nr_seq_ordem_servico_p bigint, ie_tipo_abono_p text, ds_justificativa_p text, nm_usuario_p text ) AS $body$
DECLARE

						
cd_pessoa_abono_w	man_ordem_serv_sla_abonada.cd_pessoa_abono%type;
ie_os_abonada_w		varchar(1);


BEGIN

	select	cd_pessoa_fisica
	into STRICT 	cd_pessoa_abono_w
	from	usuario
	where	nm_usuario = nm_usuario_p;
	
	select	CASE WHEN COUNT(1)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_os_abonada_w
	from 	man_ordem_serv_sla_abonada
	where	nr_seq_ordem_servico = nr_seq_ordem_servico_p;
	
	IF ((cd_pessoa_abono_w IS NOT NULL AND cd_pessoa_abono_w::text <> '') and ie_os_abonada_w = 'N') then
	
		insert into man_ordem_serv_sla_abonada(
			nr_sequencia,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			dt_atualizacao,
			cd_pessoa_abono,
			ds_justificativa,
			dt_abono,
			ie_tipo_abono,
			nr_seq_ordem_servico)
		values (
			nextval('man_ordem_serv_sla_abonada_seq'),
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			cd_pessoa_abono_w,
			ds_justificativa_p,
			clock_timestamp(),
			ie_tipo_abono_p,
			nr_seq_ordem_servico_p);
			
		commit;
		
	END IF;
	
end;
$body$
