CREATE OR REPLACE PROCEDURE man_acionar_sla_contrato ( nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type, dt_acionamento_sla_p man_ordem_servico.dt_acionamento_sla%type, nm_usuario_p text ) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Acionar as ordens de serviço com SLA.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java/HTML5) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------

Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_localizacao_w	man_localizacao.nr_sequencia%type;

dt_ordem_servico_w	man_ordem_servico.dt_ordem_servico%type;
ie_classificacao_w	man_ordem_servico.ie_classificacao%type;
ie_prioridade_w		man_ordem_servico.ie_prioridade%type;
nr_seq_ordem_servico_w	man_ordem_servico.nr_sequencia%type;

ds_historico_w		man_ordem_serv_tecnico.ds_relat_tecnico%type;

nr_seq_sla_regra_w	man_sla_regra.nr_sequencia%type;

nr_seq_tipo_hist_w	man_tipo_hist.nr_sequencia%type;

ds_macro_w		varchar(255);
ds_nome_usuario_w	varchar(255);


BEGIN

	select	max(mos.ie_classificacao),
		max(mos.nr_seq_localizacao),
		max(mos.ie_prioridade),
		max(mos.dt_ordem_servico),
		max(mos.nr_sequencia)
	into STRICT	ie_classificacao_w,
		nr_seq_localizacao_w,
		ie_prioridade_w,
		dt_ordem_servico_w,
		nr_seq_ordem_servico_w
	from	man_ordem_servico mos
	where	mos.nr_sequencia = nr_seq_ordem_serv_p;

	select	max(msr.nr_seq_tipo_hist),
		max(msr.nr_sequencia)
	into STRICT	nr_seq_tipo_hist_w,
		nr_seq_sla_regra_w
	from	man_sla ms,
		man_sla_regra msr,
		man_sla_loc msl
	where	ms.nr_sequencia = msr.nr_seq_sla
	and	ms.nr_sequencia = msl.nr_seq_sla
	and	dt_ordem_servico_w between ms.dt_vigencia and coalesce(ms.dt_fim_vigencia, clock_timestamp())
	and	msr.ie_classificacao = ie_classificacao_w
	and	msr.ie_prioridade = ie_prioridade_w
	and	msl.nr_seq_local = nr_seq_localizacao_w;

	if (	(nr_seq_sla_regra_w IS NOT NULL AND nr_seq_sla_regra_w::text <> '')
		and	(nr_seq_ordem_servico_w IS NOT NULL AND nr_seq_ordem_servico_w::text <> '')
		and	(nr_seq_tipo_hist_w IS NOT NULL AND nr_seq_tipo_hist_w::text <> '')) then
		begin

			select	coalesce(pf.nm_pessoa_fisica, u.ds_usuario, u.nm_usuario)
			into STRICT	ds_nome_usuario_W
			from	usuario u,
				pessoa_fisica pf
			where	u.nm_usuario = nm_usuario_p
			and	u.cd_pessoa_fisica = pf.cd_pessoa_fisica;
			
			select	'NM_PESSOA=' || ds_nome_usuario_w
				|| ';DT_REGISTRO=' || obter_data_extenso(dt_acionamento_sla_p, 8) || ' ' || to_char(dt_acionamento_sla_p, 'HH24:mi')
			into STRICT	ds_macro_w
			;
						
			select	obter_desc_exp_idioma(951954, coalesce(man_obter_idioma_os_local(10), 1), ds_macro_w)
			into STRICT	ds_historico_w
			;
				
			insert
			into	man_ordem_serv_tecnico(
				nr_sequencia,
				nr_seq_tipo,
				nr_seq_ordem_serv,
				nm_usuario_lib,
				nm_usuario,
				dt_liberacao,
				dt_historico,
				dt_atualizacao,
				ds_relat_tecnico
			) values (
				nextval('man_ordem_serv_tecnico_seq'),
				nr_seq_tipo_hist_w,
				nr_seq_ordem_servico_w,
				nm_usuario_p,
				nm_usuario_p,
				clock_timestamp(),
				clock_timestamp(),
				clock_timestamp(),
				ds_historico_w
			);

			update	man_ordem_servico
			set	dt_acionamento_sla = dt_acionamento_sla_p
			where	nr_sequencia = nr_seq_ordem_servico_w;

			commit;

		end;
	end if;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_acionar_sla_contrato ( nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type, dt_acionamento_sla_p man_ordem_servico.dt_acionamento_sla%type, nm_usuario_p text ) FROM PUBLIC;

