CREATE OR REPLACE PROCEDURE man_agendar_build_superior ( cd_versao_p text, ie_aplicacao_p text, dt_agenda_p timestamp, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
BEGIN

	update	man_versao_calendario x
	set	x.dt_geracao = dt_agenda_p
	where	coalesce(x.cd_build::text, '') = ''
	and	trunc(x.dt_geracao) > trunc(clock_timestamp())
	and	trunc(x.dt_geracao)  >= trunc(dt_agenda_p)
	and	ie_aplicacao = ie_aplicacao_p
	and	cd_versao = cd_versao_p
	and	ie_tipo_solicitacao = 'A';

commit;

end;
