CREATE OR REPLACE PROCEDURE man_alterar_certificado_equip (nr_sequencia_p bigint, ie_opcao_p text, ds_historico_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_opcao_p = 'L') then

update	man_calibracao
set 	dt_liberacao 	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where   nr_sequencia	= nr_sequencia_p;

insert 	into man_calibracao_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_calibracao,
		ds_historico,
		dt_liberacao)
	values (
		nextval('man_calibracao_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		Wheb_mensagem_pck.get_texto(719835,'USUARIO=' || Obter_Nome_Usuario(nm_usuario_p) || ';DIA=' || clock_timestamp()),
		clock_timestamp());

else

update	man_calibracao
set 	dt_liberacao 	 = NULL,
	nm_usuario	= nm_usuario_p
where   nr_sequencia	= nr_sequencia_p;

insert 	into man_calibracao_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_calibracao,
		ds_historico,
		dt_liberacao)
	values (
		nextval('man_calibracao_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		ds_historico_p,--Wheb_mensagem_pck.get_texto(719877,'USUARIO=' || Obter_Nome_Usuario(nm_usuario_p) || ';DIA=' || sysdate),
		clock_timestamp());
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
