CREATE OR REPLACE PROCEDURE man_alterar_datas_posicao ( nr_seq_posicao_p bigint, dt_alteracao_p timestamp, ie_tipo_data_p text, nm_usuario_p text) AS $body$
DECLARE


dt_alteracao_w		timestamp;

/*	C - Ciencia
	L - Liberação */
BEGIN

dt_alteracao_w	:= coalesce(dt_alteracao_p,clock_timestamp());

if (ie_tipo_data_p = 'C') then
	update	man_posicao_diaria_hist
	set	dt_ciencia	= dt_alteracao_w,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_posicao_p;
elsif (ie_tipo_data_p = 'L') then
	update	man_posicao_diaria_hist
	set	dt_liberacao	= dt_alteracao_w,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_posicao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
