CREATE OR REPLACE PROCEDURE man_alterar_data_fim_execucao ( nr_seq_ordem_exec_p bigint, dt_alteracao_p timestamp, ie_tipo_alteracao_p text, nm_usuario_p text) AS $body$
DECLARE


/*	F - Finalizar
	L - Limpar*/
BEGIN

if (ie_tipo_alteracao_p = 'F') then
	update	man_ordem_servico_exec
	set	dt_fim_execucao	= dt_alteracao_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_ordem_exec_p;
elsif (ie_tipo_alteracao_p = 'L') then
	update	man_ordem_servico_exec
	set	dt_fim_execucao	 = NULL,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_ordem_exec_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
