CREATE OR REPLACE PROCEDURE man_alterar_estagio_os ( nr_seq_ordem_p bigint, nr_seq_estagio_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_classificacao_w		man_ordem_servico.ie_classificacao%type;
ie_permite_erro_w		man_estagio_processo.ie_permite_erro%type;


BEGIN
if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') and (nr_seq_estagio_p IS NOT NULL AND nr_seq_estagio_p::text <> '') then
	select	max(ie_classificacao)
	into STRICT	ie_classificacao_w
	from	man_ordem_servico
	where	nr_sequencia	= nr_seq_ordem_p;

	if (ie_classificacao_w = 'E') then
		select	coalesce(max(ie_permite_erro),'N')
		into STRICT	ie_permite_erro_w
		from	man_estagio_processo
		where	nr_sequencia	= nr_seq_estagio_p;

		if (ie_permite_erro_w = 'N') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(357405);
		end if;
	end if;

	update	man_ordem_servico
	set	nr_seq_estagio	= nr_seq_estagio_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_ordem_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
