SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_alterar_grau_satisf_hist ( nr_seq_hist_p bigint, ie_grau_satisfacao_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


ds_grau_satisfacao_w		varchar(255);
dt_historico_w			timestamp;
nm_usuario_hist_w			varchar(15);
nr_seq_ordem_serv_w		bigint;
ds_historico_w			varchar(255);


BEGIN

if (coalesce(nr_seq_hist_p,0) > 0) then
	begin
	select	dt_historico,
		nm_usuario,
		nr_seq_ordem_serv,
		substr(obter_valor_dominio(1197,ie_grau_satisfacao_p),1,255)
	into STRICT	dt_historico_w,
		nm_usuario_hist_w,
		nr_seq_ordem_serv_w,
		ds_grau_satisfacao_w
	from	man_ordem_serv_tecnico
	where	nr_sequencia = nr_seq_hist_p;

	update	man_ordem_serv_tecnico
	set	ie_grau_satisfacao 	= ie_grau_satisfacao_p
	where	nr_sequencia 	= nr_seq_hist_p;

	/*ds_historico_w	:= 	'Alteração do grau de satisfação do histórico do dia ' || to_char(dt_historico_w,'dd/mm/yyyy hh24:mi:ss') || ', usuário ' ||
				nm_usuario_hist_w || ' para ' || ds_grau_satisfacao_w || chr(13) || chr(10) || 'Motivo: ' || ds_observacao_p; */
	ds_historico_w	:= 	WHEB_MENSAGEM_PCK.get_texto(456169,'dt_historico_w='|| dt_historico_w ||';nm_usuario_hist_w='|| nm_usuario_hist_w ||
	';ds_grau_satisfacao_w='|| ds_grau_satisfacao_w || chr(13) || chr(10) ||';ds_observacao_p='|| ds_observacao_p);

	insert into man_ordem_serv_tecnico(
				nr_sequencia,
				nr_seq_ordem_serv,
				dt_atualizacao,
				nm_usuario,
				ds_relat_tecnico,
				dt_historico,
				ie_origem,
				dt_liberacao,
				nm_usuario_lib)
			values (	nextval('man_ordem_serv_tecnico_seq'),
				nr_seq_ordem_serv_w,
				clock_timestamp(),
				nm_usuario_p,
				ds_historico_w,
				clock_timestamp(),
				'I',
				clock_timestamp(),
				nm_usuario_p);
	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
