CREATE OR REPLACE PROCEDURE man_alterar_grupo_os_story ( nr_story_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_ordem_servico_w	desenv_story_sprint.nr_ordem_servico%type;
nr_team_w		desenv_story.nr_team%type;
nr_seq_grupo_des_w	desenv_team.nr_seq_grupo_des%type;


BEGIN

select	max(nr_ordem_servico)
into STRICT	nr_ordem_servico_w
from (SELECT	nr_ordem_servico
	from	desenv_story_sprint
	where	nr_story = nr_story_p
	order by nr_sequencia desc) alias1 LIMIT 1;

if (nr_ordem_servico_w IS NOT NULL AND nr_ordem_servico_w::text <> '') then
	select	nr_team
	into STRICT	nr_team_w
	from	desenv_story
	where	nr_sequencia = nr_story_p;

	select	nr_seq_grupo_des
	into STRICT	nr_seq_grupo_des_w
	from	desenv_team
	where	nr_sequencia = nr_team_w;

	update	man_ordem_servico
	set	nr_seq_grupo_des = nr_seq_grupo_des_w
	where	nr_sequencia = nr_ordem_servico_w;
end if;

commit;

end;
