CREATE OR REPLACE PROCEDURE man_altera_classif_nova ( nr_seq_ordem_p bigint, ds_justificativa_p text, nr_seq_classif_cad_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then

update  man_ordem_servico
set     nr_seq_classif_cad = nr_seq_classif_cad_p,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where   nr_sequencia = nr_seq_ordem_p;


if (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') then
	insert into man_ordem_serv_tecnico(	nr_sequencia,
		nr_seq_ordem_serv,
		dt_atualizacao,
		nm_usuario,
		ds_relat_tecnico,
		dt_historico,
		dt_liberacao,
		nm_usuario_lib,
		nr_seq_tipo,
		ie_origem)
	values (nextval('man_ordem_serv_tecnico_seq'),
		nr_seq_ordem_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_justificativa_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		148,
		'I');
end if;
		
commit;

end if;

end;
$body$
