CREATE OR REPLACE PROCEDURE man_aprovar_cronograma ( nr_seq_cronograma_p bigint, qt_horas_p bigint default null, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


dt_aprovacao_w		timestamp;			
	

BEGIN

select	max(dt_aprovacao)
into STRICT	dt_aprovacao_w
from 	proj_cronograma
where	nr_sequencia	= nr_seq_cronograma_p;

if (coalesce(dt_aprovacao_w::text, '') = '') then
	begin
	CALL proj_atual_pr_previsto_cron(nr_seq_cronograma_p, nm_usuario_p);
	
	update	proj_cronograma
	set	dt_aprovacao	= clock_timestamp(),
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		nm_usuario_aprov= nm_usuario_p
	where	nr_sequencia	= nr_seq_cronograma_p;
	end;
else
	begin
	update	proj_cronograma
	set	dt_aprovacao	 = NULL,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		nm_usuario_aprov = NULL
	where	nr_sequencia	= nr_seq_cronograma_p;
	
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
