CREATE OR REPLACE PROCEDURE man_aprovar_faq ( nr_seq_faq_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


qt_com_faq_aprov_w	com_faq_aprov.nr_sequencia%type;


BEGIN

if (nr_seq_faq_p IS NOT NULL AND nr_seq_faq_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	update	com_faq_aprov
	set	dt_aprovacao = clock_timestamp()
	where	nr_seq_faq = nr_seq_faq_p
	and	cd_pessoa_aprovacao = cd_pessoa_fisica_p;

	select	count(*)
	into STRICT	qt_com_faq_aprov_w
	from	com_faq_aprov
	where	nr_seq_faq = nr_seq_faq_p
	and	coalesce(dt_aprovacao::text, '') = '';

	if (qt_com_faq_aprov_w = 0) then
		update	com_faq
		set	dt_aprovacao = clock_timestamp()
		where	nr_sequencia = nr_seq_faq_p;
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
