CREATE OR REPLACE PROCEDURE man_aprovar_orcamento ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_sequencia_p > 0) then 
	begin 
	update	man_ordem_servico_orc 
	set	dt_aprovacao 		= clock_timestamp(), 
		nm_usuario_aprovacao	= nm_usuario_p, 
		nm_pessoa_aprovacao	= substr(obter_pf_usuario(nm_usuario_p,'N'),1,40) 
	where	nr_sequencia 		= nr_sequencia_p;
 
	commit;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
