CREATE OR REPLACE PROCEDURE man_atribuir_epic_art ( nr_epic_p bigint, nr_art_p bigint, nr_art_atual_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


nr_art_planejado_w	desenv_art.nr_sequencia%type;
dt_art_planejado_w	desenv_art.dt_inicial%type;
dt_art_final_plan_w	desenv_art.dt_final%type;
dt_final_w		desenv_art.dt_final%type;
qt_existe_w		bigint;
qt_features_art_w	bigint;


BEGIN

select	max(dt_final)
into STRICT	dt_final_w
from	desenv_art
where	nr_sequencia = nr_art_p;

if (dt_final_w < clock_timestamp()) then
	ds_erro_p := wheb_mensagem_pck.get_texto(336110); --Este ART já terminou.
	return;
end if;

select	count(*)
into STRICT	qt_existe_w
from	desenv_epic_art a,
	desenv_art b
where	nr_epic = nr_epic_p
and	a.nr_art = b.nr_sequencia
and	((nr_art_p = a.nr_art) or (not clock_timestamp() between b.dt_inicial and b.dt_final))
and	coalesce(b.nr_sequencia,0) <> coalesce(nr_art_atual_p,0);

if (qt_existe_w > 0) then
	ds_erro_p := wheb_mensagem_pck.get_texto(336111); --Esta Feature já foi planejada.
	return;
end if;

select	max(a.nr_art),
	max(b.dt_inicial),
	max(b.dt_final)
into STRICT	nr_art_planejado_w,
	dt_art_planejado_w,
	dt_art_final_plan_w
from	desenv_epic_art a,
	desenv_art b
where	a.nr_art = b.nr_sequencia
and	a.nr_epic = nr_epic_p
and	b.nr_sequencia = nr_art_atual_p;

select	count(*)
into STRICT	qt_features_art_w
from	desenv_feature_release a,
	desenv_release b,
	desenv_feature c
where	b.nr_art = nr_art_atual_p
and	a.nr_feature = c.nr_sequencia
and	c.nr_epic = nr_epic_p
and	(a.nr_release IS NOT NULL AND a.nr_release::text <> '');

if (dt_art_planejado_w > clock_timestamp()) or
	(dt_art_planejado_w IS NOT NULL AND dt_art_planejado_w::text <> '' AND qt_features_art_w = 0) then
	update	desenv_epic_art
	set	nr_art = nr_art_p
	where	nr_art = nr_art_planejado_w
	and	nr_epic = nr_epic_p;
else
	delete	from desenv_epic_art
	where	nr_epic = nr_epic_p
	and	coalesce(nr_art::text, '') = '';

	insert	into desenv_epic_art(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_epic,
		nr_art)
	values (	nextval('desenv_epic_art_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_epic_p,
		nr_art_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_atribuir_epic_art ( nr_epic_p bigint, nr_art_p bigint, nr_art_atual_p bigint, nm_usuario_p text, ds_erro_p INOUT text) FROM PUBLIC;

