CREATE OR REPLACE PROCEDURE man_atualizar_data_prev_cron (nr_seq_cronograma_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_inicio_prev_w	timestamp;
dt_fim_prev_w		timestamp;
nr_seq_projeto_w	bigint;


BEGIN
select	min(dt_inicio_prev)
into STRICT	dt_inicio_prev_w
from	proj_cron_etapa
where	nr_seq_cronograma = nr_seq_cronograma_p;

select	max(dt_fim_prev)
into STRICT	dt_fim_prev_w
from	proj_cron_etapa
where	nr_seq_cronograma = nr_seq_cronograma_p;


if (dt_inicio_prev_w IS NOT NULL AND dt_inicio_prev_w::text <> '') and (dt_fim_prev_w IS NOT NULL AND dt_fim_prev_w::text <> '') then
	update	proj_cronograma
	set	dt_inicio = dt_inicio_prev_w,
		dt_fim	  = dt_fim_prev_w
	where	nr_sequencia = nr_seq_cronograma_p;
end if;


select	max(nr_seq_proj)
into STRICT	nr_seq_projeto_w
from	proj_cronograma
where	nr_sequencia = nr_seq_cronograma_p;

select	min(dt_inicio),
	max(dt_fim)
into STRICT	dt_inicio_prev_w,
	dt_fim_prev_w
from	proj_cronograma
where	ie_situacao = 'A'
and	nr_seq_proj = nr_seq_projeto_w;

if (dt_inicio_prev_w IS NOT NULL AND dt_inicio_prev_w::text <> '') and (dt_fim_prev_w IS NOT NULL AND dt_fim_prev_w::text <> '') then
	update	proj_projeto
	set	dt_inicio_prev = dt_inicio_prev_w,
		dt_fim_prev	  = dt_fim_prev_w
	where	nr_sequencia = nr_seq_projeto_w;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
