SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_atualizar_processo ( nr_seq_ordem_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


/* Ação I - Iniciar Processo
        F - Finalizar Processo  */
BEGIN

if (ie_acao_p = 'I') then
	update		man_ordem_serv_sla
	set		dt_inicio		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where      	nr_seq_ordem		= nr_seq_ordem_p;
elsif (ie_acao_p = 'F') then
	update		man_ordem_serv_sla
	set		dt_termino		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where      	nr_seq_ordem		= nr_seq_ordem_p;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
