CREATE OR REPLACE PROCEDURE man_atualizar_status_feature ( nr_feature_p bigint, ie_status_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_stories_w	bigint;


BEGIN

if (ie_status_p = 'P') then
	select	count(*)
	into STRICT	qt_stories_w
	from	desenv_story
	where	nr_feature = nr_feature_p;

	if (qt_stories_w = 0) then
		ds_erro_p := wheb_mensagem_pck.get_texto(354793);
		return;
	end if;
end if;

update	desenv_feature
set	ie_status = ie_status_p,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_feature_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
