CREATE OR REPLACE PROCEDURE man_atualizar_ult_visao ( nr_seq_ordem_p bigint, nm_usuario_p text, ie_operacao_p text) AS $body$
BEGIN

if (ie_operacao_p = 'A') then
	update	man_ordem_servico_Exec
	set	dt_ult_visao		= clock_timestamp()
	where	nr_seq_ordem		= nr_seq_ordem_p
	and	nm_usuario_exec	= nm_usuario_p;

elsif (ie_operacao_p = 'L') then
	update	man_ordem_servico_Exec
	set	dt_ult_visao		 = NULL
	where	nr_seq_ordem		= nr_seq_ordem_p
	and	nm_usuario_exec	= nm_usuario_p;
end if;

commit;

END;
$body$
