SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_atualiza_dados_os_vinc ( nr_seq_ordem_p bigint, nr_seq_causa_dano_p bigint, nr_seq_complex_p bigint, nr_seq_estagio_p bigint, nr_seq_tipo_solucao_p bigint, nr_seq_tipo_ordem_p bigint, ie_encerrar_os_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then
	begin

	update	man_ordem_servico x
	set	nr_seq_causa_dano	= CASE WHEN coalesce(nr_seq_causa_dano_p,0)=0 THEN nr_seq_causa_dano  ELSE nr_seq_causa_dano_p END ,
		nr_seq_complex 		= CASE WHEN coalesce(nr_seq_complex_p,0)=0 THEN nr_seq_complex  ELSE nr_seq_complex_p END ,
		nr_seq_estagio		= CASE WHEN coalesce(nr_seq_estagio_p,0)=0 THEN nr_seq_estagio  ELSE nr_seq_estagio_p END ,
		nr_seq_tipo_solucao	= CASE WHEN coalesce(nr_seq_tipo_solucao_p,0)=0 THEN nr_seq_tipo_solucao  ELSE nr_seq_tipo_solucao_p END ,
		nr_seq_tipo_ordem		= CASE WHEN coalesce(nr_seq_tipo_ordem_p,0)=0 THEN nr_seq_tipo_ordem  ELSE nr_seq_tipo_ordem_p END ,

		x.dt_atualizacao 		= clock_timestamp(),
		x.nm_usuario 		= nm_usuario_p
	where	x.nr_sequencia in (	SELECT	a.nr_sequencia
				from	man_ordem_servico a,
					man_ordem_serv_vinc b
				where	a.nr_sequencia = b.nr_seq_os_vinculada
				and	a.ie_status_ordem <> '3'
				and	coalesce(a.dt_fim_real::text, '') = ''
				and	b.nr_seq_ordem_servico = nr_seq_ordem_p)
	and	x.ie_status_ordem <> '3';

	if (coalesce(ie_encerrar_os_p,'N') = 'S') then
		begin

		update	man_ordem_servico x
		set	x.ie_status_ordem = 3,
			x.dt_atualizacao = clock_timestamp(),
			x.nm_usuario = nm_usuario_p,
			x.dt_fim_real = clock_timestamp()
			where  x.nr_sequencia in (SELECT	a.nr_sequencia
						from	man_ordem_servico a,
							man_ordem_serv_vinc b
						where	a.nr_sequencia = b.nr_seq_os_vinculada
						and	b.nr_seq_ordem_servico =  nr_seq_ordem_p);

		end;
	end if;

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
