CREATE OR REPLACE PROCEDURE man_atualiza_equip_parado_fim (nm_usuario_p text, nr_seq_equipamento_p bigint) AS $body$
BEGIN
	if (nr_seq_equipamento_p IS NOT NULL AND nr_seq_equipamento_p::text <> '') then
		begin
		update	man_equip_periodo_parado
		set	dt_periodo_final     = clock_timestamp(),
			dt_atualizacao       = clock_timestamp(),
			nm_usuario           = nm_usuario_p
		where	nr_seq_equipamento   = nr_seq_equipamento_p
		and	coalesce(dt_periodo_final::text, '') = '';
		commit;
		end;
	end if;

commit;

end;
$body$
