CREATE OR REPLACE PROCEDURE man_atualiza_estagio_os_solic ( nr_seq_ordem_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_estagio_w	bigint;
ie_existe_w	varchar(1);


BEGIN

if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then
	begin

	select	coalesce(max((obter_valor_param_usuario(299,351,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p))::numeric ),null)
	into STRICT	nr_seq_estagio_w
	;

	if (nr_seq_estagio_w IS NOT NULL AND nr_seq_estagio_w::text <> '') then
		begin

		select	coalesce(max('S'),'N')
		into STRICT	ie_existe_w
		from	man_estagio_processo
		where	nr_sequencia = nr_seq_estagio_w;

		if (ie_existe_w = 'S') then
			begin

			update	man_ordem_servico
			set	nr_seq_estagio = nr_seq_estagio_w,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	nr_sequencia = nr_seq_ordem_p;

			commit;

			end;
		end if;
		end;
	end if;
	end;
end if;

end;
