CREATE OR REPLACE PROCEDURE man_atualiza_fim_ativ ( nr_seq_ordem_p bigint, dt_fim_atividade_p timestamp) AS $body$
BEGIN

if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then

	update	man_ordem_serv_ativ
	set	dt_fim_atividade = dt_fim_atividade_p,
		qt_minuto = coalesce(obter_min_entre_datas(dt_atividade,dt_fim_atividade_p, 1),0)
	where	nr_seq_ordem_serv = nr_seq_ordem_p
	and	coalesce(dt_fim_atividade::text, '') = '';

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
