CREATE OR REPLACE PROCEDURE man_atualiza_fim_real_ativ ( nr_seq_ordem_p bigint, nm_usuario_p text, ie_opcao_p text, ie_opcao_ativ_p text) AS $body$
DECLARE


dt_atual_w	timestamp := clock_timestamp();
		

BEGIN

if (ie_opcao_p = 'Q') then
	update 	man_ordem_serv_ativ
	set	dt_fim_atividade 	= greatest(dt_atual_w,dt_atividade),
		dt_atualizacao		= dt_atual_w,
		nm_usuario		= nm_usuario_p,
		qt_minuto		= obter_min_entre_datas(dt_atividade,greatest(dt_atual_w,dt_atividade),1),
		qt_minuto_cobr		= obter_min_entre_datas(dt_atividade,greatest(dt_atual_w,dt_atividade),1)
	where	nr_seq_ordem_serv	= nr_seq_ordem_p
	and	nm_usuario_exec 	= nm_usuario_p
	and	coalesce(dt_fim_atividade::text, '') = ''
	and	((PKG_DATE_UTILS.START_OF(dt_atividade, 'DD') = PKG_DATE_UTILS.START_OF(dt_atual_w, 'DD'))
		or	'S' = ie_opcao_ativ_p);
elsif (ie_opcao_p = 'T') then
	update	man_ordem_serv_ativ
	set	dt_fim_atividade 	= greatest(dt_atual_w,dt_atividade),
		dt_atualizacao		= dt_atual_w,
		nm_usuario		= nm_usuario_p,
		qt_minuto		= obter_min_entre_datas(dt_atividade,greatest(dt_atual_w,dt_atividade),1),
		qt_minuto_cobr		= obter_min_entre_datas(dt_atividade,greatest(dt_atual_w,dt_atividade),1)
	where	nr_seq_ordem_serv	= nr_seq_ordem_p
	and	coalesce(dt_fim_atividade::text, '') = ''
	and	((PKG_DATE_UTILS.START_OF(dt_atividade, 'DD') = PKG_DATE_UTILS.START_OF(dt_atual_w, 'DD'))
		or	'S' = ie_opcao_ativ_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
