CREATE OR REPLACE PROCEDURE man_atualiza_os_processo_hist ( nr_sequencia_p bigint, nr_seq_ordem_serv_p bigint, ie_origem_os_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
	ie_origem_os_p
	1 - Sistemas Philips
	2 - BSC
	3 - Serviços
	4 - Manutenção
	5 - Tarefa
	6 - Qualidade

	AS CONSISTÊNCIAS DEVERÃO SER IMPLEMENTADAS CONFORME AS NECESSIDADES

-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_reg_w			bigint;
ds_erro_w			varchar(255);
ie_inicio_real_proc_w		varchar(15);


BEGIN

if (ie_origem_os_p = '4') then
	ie_inicio_real_proc_w := obter_param_usuario(299, 61, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_inicio_real_proc_w);
end if;

if (coalesce(ie_inicio_real_proc_w,'0') = '0') then
	ie_inicio_real_proc_w	:= 'S';
end if;

select	count(1)
into STRICT	qt_reg_w
from	man_tipo_hist		b,
	man_ordem_serv_tecnico	a
where	b.nr_sequencia					= a.nr_seq_tipo
and	coalesce(b.ie_atualiza_os_proc_prim_hist,'N')	= 'S'
and	a.nr_seq_ordem_serv				= nr_seq_ordem_serv_p
and	a.nr_sequencia					= nr_sequencia_p  LIMIT 1;

if (qt_reg_w > 0) then
	begin
	begin
	update	man_ordem_servico
	set	ie_status_ordem		= '2',
		dt_inicio_previsto	= coalesce(dt_inicio_previsto,coalesce(dt_inicio_desejado,dt_ordem_servico)),
		dt_fim_previsto		= coalesce(dt_fim_previsto,dt_conclusao_desejada),
		dt_inicio_real		= coalesce(dt_inicio_real,CASE WHEN ie_inicio_real_proc_w='S' THEN clock_timestamp() END ),
		nm_usuario_exec		= nm_usuario_p
	where	nr_sequencia		= nr_seq_ordem_serv_p;
	exception
	when others then
		ds_erro_w := substr(wheb_mensagem_pck.get_texto(281046) || chr(13) || chr(10) || chr(13) || chr(10) || sqlerrm(SQLSTATE),1,255);
	end;

	ds_erro_p	:= ds_erro_w;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
