SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_atual_estagio_ret_os_web ( nr_seq_ordem_servico_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_ordem_servico_p > 0) then
	begin
	update	man_ordem_servico a
	set	a.nr_seq_estagio = (SELECT	CASE WHEN b.ie_aguarda_cliente='S' THEN  coalesce(b.nr_seq_retorno_ws,b.nr_sequencia)  ELSE b.nr_sequencia END
				FROM	man_estagio_processo b
				WHERE	b.nr_sequencia = a.nr_seq_estagio),
		a.ie_status_ordem	= CASE WHEN a.ie_status_ordem=3 THEN 2  ELSE a.ie_status_ordem END ,
		a.dt_atualizacao	= clock_timestamp(),
		a.nm_usuario	= nm_usuario_p
	where	a.nr_sequencia	= nr_seq_ordem_servico_p;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
