CREATE OR REPLACE PROCEDURE man_atual_pos_analise_vv ( nr_seq_analise_p bigint, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_classif_w		bigint;
ie_severidade_w			varchar(3);
ie_equipe_w			varchar(3);
ie_posicionamento_w		varchar(1);


BEGIN

select	nr_seq_classificacao,
	ie_severidade,
	ie_equipe
into STRICT	nr_seq_classif_w,
	ie_severidade_w,
	ie_equipe_w
from	man_ordem_serv_analise_vv
where	nr_sequencia = nr_seq_analise_p;

select	coalesce(max(ie_posicionamento), 'N')
into STRICT	ie_posicionamento_w
from	man_analise_vv_classif
where	nr_seq_classificacao = nr_seq_classif_w
and	ie_severidade = ie_severidade_w
and	ie_equipe = ie_equipe_w
and	ie_situacao = 'A';

update	man_ordem_serv_analise_vv
set	ie_posicionamento = ie_posicionamento_w,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_seq_analise_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
