SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_avisar_exec_prevent_atraso () AS $body$
DECLARE



nr_sequencia_w		bigint;
nm_usuario_exec_w	varchar(15);
ds_titulo_w		varchar(255);
ds_mensagem_w		varchar(255);
dt_conclusao_desejada_w	timestamp;
nr_seq_classif_w		bigint;


C01 CURSOR FOR
SELECT	nr_sequencia,
	dt_conclusao_desejada
from	man_ordem_servico
where	ie_tipo_ordem = 2	/*preventivas*/
and	(dt_conclusao_desejada IS NOT NULL AND dt_conclusao_desejada::text <> '')
and	dt_conclusao_desejada < clock_timestamp()
and	ie_status_ordem <> 3;

C02 CURSOR FOR
SELECT	nm_usuario_exec
from	man_ordem_servico_exec
where	nr_seq_ordem 	= nr_sequencia_w;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	dt_conclusao_desejada_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nm_usuario_exec_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		select	obter_classif_comunic('F')
		into STRICT	nr_seq_classif_w
		;

		ds_titulo_w	:= 'Ordem de serviço preventiva atrasada';
		ds_mensagem_w	:= substr('A ordem de serviço nº ' || nr_sequencia_w || ' está atrasada.' || chr(13) ||
				'Data de conclusão desejada: ' || dt_conclusao_desejada_w,1,255);

		CALL gerar_comunic_padrao(
				clock_timestamp(),
				ds_titulo_w,
				ds_mensagem_w,
				'Tasy',
				'N',
				nm_usuario_exec_w,
				'N',
				nr_seq_classif_w,
				'',
				'',
				'',
				clock_timestamp(),
				'',
				'');
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_avisar_exec_prevent_atraso () FROM PUBLIC;

