SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_cadastrar_versao_testes ( cd_versao_vv_p text, nr_seq_ordem_serv_p bigint, ds_motivo_p text, ie_aplicacao_p text, ds_lista_projeto_p text, cd_ramal_p text, cd_pessoa_fisica_p text, ds_lista_alteracao_p text, ds_lista_classe_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_man_os_ctrl_desc_w 	bigint := 0;
nr_seq_man_os_ctrl_build_w 	bigint := 0;
nr_seq_man_os_ctrl_build_alt_w	bigint := 0;
ds_lista_projeto_w		varchar(2000);
ds_projeto_w			varchar(100);
ds_lista_alteracao_w		varchar(2000);
ds_alteracao_w			varchar(2000);
ds_lista_classe_w		varchar(2000);
ds_classe_w			varchar(2000);
nm_usuarilo_lider_w		varchar(100);


BEGIN

if (cd_versao_vv_p IS NOT NULL AND cd_versao_vv_p::text <> '') and (nr_seq_ordem_serv_p IS NOT NULL AND nr_seq_ordem_serv_p::text <> '') and (ds_motivo_p IS NOT NULL AND ds_motivo_p::text <> '') and (ie_aplicacao_p IS NOT NULL AND ie_aplicacao_p::text <> '') and (ds_lista_projeto_p IS NOT NULL AND ds_lista_projeto_p::text <> '') and (cd_ramal_p IS NOT NULL AND cd_ramal_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	nm_usuario_lider
	into STRICT	nm_usuarilo_lider_w
	from	grupo_desenvolvimento
	where	nr_sequencia = obter_grupo_usuario_wheb(nm_usuario_p);

	select	nextval('man_os_ctrl_desc_seq')
	into STRICT	nr_seq_man_os_ctrl_desc_w
	;

	insert	into	man_os_ctrl_desc( nr_sequencia,
					  dt_atualizacao,
					  nm_usuario,
					  nr_seq_ordem_serv,
					  ds_alteracao,
					  ie_aplicacao)
				values ( nr_seq_man_os_ctrl_desc_w,
					  clock_timestamp(),
					  nm_usuario_p,
					  nr_seq_ordem_serv_p,
					  ds_motivo_p,
					  ie_aplicacao_p );
	commit;

	select	nextval('man_os_ctrl_build_seq')
	into STRICT	nr_seq_man_os_ctrl_build_w
	;
	
	insert	into	man_os_ctrl_build( nr_sequencia,
					   dt_atualizacao,
					   nm_usuario,
					   cd_versao,
					   nr_seq_man_os_ctrl_desc,
					   ie_situacao)
				values ( nr_seq_man_os_ctrl_build_w,
					   clock_timestamp(),
					   nm_usuario_p,
					   cd_versao_vv_p,
					   nr_seq_man_os_ctrl_desc_w,
					   'A');

	commit;


	--loop responsavel pelas bind variables
	ds_lista_projeto_w := ds_lista_projeto_p;
	ds_lista_alteracao_w := ds_lista_alteracao_p;
	ds_lista_classe_w := ds_lista_classe_p;

	LOOP
		if (ds_lista_projeto_w = '') or (coalesce(ds_lista_projeto_w::text, '') = '') or (ds_lista_alteracao_w = '') or (coalesce(ds_lista_alteracao_w::text, '') = '') or (ds_lista_classe_w = '') or (coalesce(ds_lista_classe_w::text, '') = '')then
			EXIT;
		end	if;

		--ds_lista_projeto_w = todos os projetos adicionados
		--ds_projeto_w = parametro a ser analisado atualmente
		ds_projeto_w	:= substr(ds_lista_projeto_w, 1, position('#;' in ds_lista_projeto_w) - 1);
		--ds_lista_projeto_w :=	substr(ds_lista_projeto_w, 1, instr(ds_projeto_w, '#;') - 1);
		ds_lista_projeto_w :=	substr(ds_lista_projeto_w, position('#;' in ds_lista_projeto_w)+2, length(ds_lista_projeto_w) - 1);

		--ds_lista_alteracao_w	 = todos as documentaçoes de alterações
		--ds_alteracao_w = parametro a ser analisado atualmente
		ds_alteracao_w	:= substr(ds_lista_alteracao_w	, 1, position('#;' in ds_lista_alteracao_w	) - 1);
		--ds_lista_alteracao_w :=	substr(ds_lista_alteracao_w, 1, instr(ds_alteracao_w, '#;') - 1);
		ds_lista_alteracao_w :=	substr(ds_lista_alteracao_w, position('#;' in ds_lista_alteracao_w)+2, length(ds_lista_alteracao_w) - 1);

		--ds_lista_classe_w= todos as classes alteradas
		--ds_classe_w = parametro a ser analisado atualmente
		ds_classe_w	:= substr(ds_lista_classe_w, 1, position('#;' in ds_lista_classe_w) - 1);
		--ds_lista_classe_w	:= substr(ds_lista_classe_w, 1, instr(ds_classe_w, '#;') - 1);
		ds_lista_classe_w :=	substr(ds_lista_classe_w, position('#;' in ds_lista_classe_w)+2, length(ds_lista_classe_w) - 1);


		select	nextval('man_os_ctrl_build_alt_seq')
		into STRICT	nr_seq_man_os_ctrl_build_alt_w
		;

		insert into	man_os_ctrl_build_alt(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							ds_projeto,
							cd_ramal,
							ds_alteracao,
							ds_classe,
							nr_seq_man_os_ctrl_build,
							cd_pessoa_fisica)
					values (	nr_seq_man_os_ctrl_build_alt_w,
							clock_timestamp(),
							nm_usuario_p,
							ds_projeto_w,
							cd_ramal_p,
							ds_alteracao_w,
							ds_classe_w,
							nr_seq_man_os_ctrl_build_w,
							cd_pessoa_fisica_p );

		commit;


	END	LOOP;


	update	man_os_ctrl_desc
	set	nm_usuario_lib = nm_usuarilo_lider_w,
		dt_liberacao = clock_timestamp()
	where 	nr_sequencia = nr_seq_man_os_ctrl_desc_w;

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_cadastrar_versao_testes ( cd_versao_vv_p text, nr_seq_ordem_serv_p bigint, ds_motivo_p text, ie_aplicacao_p text, ds_lista_projeto_p text, cd_ramal_p text, cd_pessoa_fisica_p text, ds_lista_alteracao_p text, ds_lista_classe_p text, nm_usuario_p text) FROM PUBLIC;

