CREATE OR REPLACE PROCEDURE man_calcular_preco_ativ_pj ( nr_sequencia_p bigint) AS $body$
DECLARE


dt_atividade_w				timestamp;
ie_calcula_valor_w			varchar(1);
nm_usuario_w				varchar(15);
nr_seq_controle_w			bigint;
nr_seq_funcao_w				bigint;
vl_preco_w				double precision;
vl_venda_w				double precision;

c01 CURSOR FOR
	SELECT	coalesce(vl_preco,0),
		coalesce(vl_preco_venda,0)
	from 	man_preco_funcao_pj
	where	nr_seq_funcao	= nr_seq_funcao_w
	and	trunc(dt_atividade_w)	between	coalesce(dt_inicio_vigencia,trunc(clock_timestamp())) and coalesce(fim_dia(dt_fim_vigencia),trunc(clock_timestamp()))
	order by	dt_inicio_vigencia;


BEGIN

begin
select	coalesce(c.ie_calcula_valor,'N'),
	a.nr_seq_funcao,
	a.dt_atividade,
	a.nm_usuario
into STRICT	ie_calcula_valor_w,
	nr_seq_funcao_w,
	dt_atividade_w,
	nm_usuario_w
from	man_localizacao c,
	man_ordem_servico b,
	man_ordem_serv_ativ_pj a
where	a.nr_seq_ordem_servico 	= b.nr_sequencia
and	b.nr_seq_localizacao	= c.nr_sequencia
and	a.nr_sequencia		= nr_sequencia_p;
exception
when others then
	ie_calcula_valor_w := 'N';
end;

if (ie_calcula_valor_w = 'S') then
	begin
	open c01;
	loop
	fetch c01 into
		vl_preco_w,
		vl_venda_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		vl_preco_w		:= vl_preco_w;
	end loop;
	close c01;
	update	man_ordem_serv_ativ_pj
	set	vl_cobranca		= vl_preco_w * coalesce(qt_minuto_cobr,0) / 60,
		vl_cobranca_venda	= vl_venda_w * coalesce(qt_minuto_cobr,0) / 60
	where	nr_sequencia		= nr_sequencia_p;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
