SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_cancelar_os_anomalia ( nr_sequencia_p bigint, ds_justif_cancel_p text, nm_usuario_p text) AS $body$
DECLARE


	ds_relat_tecnico_w 		varchar(4000);

BEGIN

	update	man_ordem_servico
	set	dt_inicio_real		= coalesce(dt_inicio_real, dt_inicio_previsto),
		dt_fim_real		= clock_timestamp(),
		nr_seq_estagio		= 9, 	-- ok cliente - encerrado
		ie_status_ordem		= 3, 	-- encerrada
		nr_grupo_trabalho       = 132,  -- Teste de Software
		nr_seq_tipo_solucao 	= 549, 	-- Cliente - Cancelou
		nr_seq_motivo_cancel	= 7, 	-- aberta indevidamente
		ie_grau_satisfacao 		= 'N', 	-- nao se aplica
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario_encer		= nm_usuario_p,
		ie_potencial_privacy 	= 'N',
		ds_potencial_privacy 	 = NULL,
		ie_potencial_security 	= 'N',
		ds_potencial_security 	 = NULL,
		ie_complaint 		= 'N',
		nr_customer_requirement 	 = NULL,
		ie_potential_harmed 	= 'N',
		ds_potential_harmed 	 = NULL,
		ie_potencial_safety 		= 'N',
		ds_potencial_safety 	 = NULL,
		ie_malfunction 		= 'N',
		ds_malfunction 		 = NULL,
		ie_severity_harm 		 = NULL,
		ie_probability_harm 		 = NULL
	where	nr_sequencia		= nr_sequencia_p;

	commit;
	
	ds_relat_tecnico_w := substr(obter_desc_expressao(294844, 'Ordem cancelada') || '.' || chr(10) || chr(13)
			|| obter_desc_expressao(327458, 'Motivo:') || ' ' || ds_justif_cancel_p, 1, 4000);

	CALL man_gravar_historico_ordem(
			nr_seq_ordem_serv_p 	=> nr_sequencia_p,
			dt_liberacao_p 		=> clock_timestamp(),
			ds_relat_tecnico_p 		=> ds_relat_tecnico_w,
			ie_origem_p 		=> 'I',
			nr_seq_tipo_p 		=> 1, -- informacoes relevantes da os
			nm_usuario_p 		=> nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
