SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_comunic_alteracao_satisf (nr_sequencia_p bigint, nm_usuario_p text, ds_justificativa_p text, ie_opcao_p text) AS $body$
DECLARE


ds_titulo_w				varchar(255);
ds_comunicado_w			varchar(4000);
nr_sequencia_w			bigint;
nr_seq_classif_w		bigint;
nm_usuario_w			varchar(15);
ds_dano_breve_w			varchar(80);
ds_grau_satisfacao_w	varchar(80);
nm_usuario_exec_w		varchar(15);
ds_comunic_exec_w		varchar(2000);
nr_grupo_trabalho_w		bigint;

/* Ie_opcao_p
'N' - Nao envia
'S' - Solicitante
'R' - Responsavel setor
'E' - Executor
'T' - Todos
'G' - Responsavel pelo grupo de trabalho
'A' - Todos os responsaveis (Setor e Grupo de trabalho)
*/
C01 CURSOR FOR

SELECT	distinct(nm_usuario)
from (
	SELECT	nm_usuario_exec nm_usuario
	from	man_ordem_servico_exec
	where	nr_seq_ordem		= nr_sequencia_p
	and	ie_opcao_p		in ('E','T')
	
union all

	select	min(b.nm_usuario) nm_usuario
	from	usuario b,
		man_ordem_servico a
	where	a.nr_sequencia		= nr_sequencia_p
	and	b.cd_pessoa_fisica		= a.cd_pessoa_solicitante
	and	ie_opcao_p		in ('S','T')
	
union all

	select	substr(obter_usuario_pessoa(a.cd_pessoa_resp),1,60) nm_usuario
	from	setor_atendimento a,
		man_ordem_servico_exec c
	where	a.cd_setor_atendimento = obter_setor_usuario(c.nm_usuario_exec)
	and 	c.nr_seq_ordem		= nr_sequencia_p
	and	ie_opcao_p		in ('R','T','A')
	
union all

	select	nm_usuario_param nm_usuario
	from	man_grupo_trab_usuario
	where	coalesce(ie_responsavel,'N') = 'S'
	and	nr_seq_grupo_trab	= nr_grupo_trabalho_w
	and	ie_opcao_p		in ('T','G','A')) alias10;
	
c02 CURSOR FOR
	SELECT	distinct
		nm_usuario_exec
	from (
		SELECT	nm_usuario_exec
		from	man_ordem_servico_exec
		where	nr_seq_ordem = nr_sequencia_p
		
union all

		select	nm_usuario_exec
		from	man_ordem_serv_ativ
		where	nr_seq_ordem_serv = nr_sequencia_p) alias0;


BEGIN

select	ds_dano_breve, --NOSONAR
	substr(obter_desc_expressao(290975)||': '|| substr(obter_valor_dominio(1197,ie_grau_satisfacao),1,40),1,80),
	nr_grupo_trabalho
into STRICT	ds_dano_breve_w,
	ds_grau_satisfacao_w,
	nr_grupo_trabalho_w
from	man_ordem_servico
where	nr_sequencia = nr_sequencia_p;

ds_dano_breve_w := substr(obter_desc_expressao(855500)||': '|| ds_dano_breve_w,1,80);

ds_titulo_w	:= obter_desc_expressao(1075874)||' '|| nr_sequencia_p;

select	obter_classif_comunic('F')
into STRICT	nr_seq_classif_w
;

ds_comunicado_w	:= substr(ds_dano_breve_w || chr(13) || chr(10) || ds_grau_satisfacao_w || chr(13) || chr(10) ||
		  		obter_desc_expressao(1075878)||': '|| chr(13) || chr(10) ||   
		   		ds_justificativa_p,1,4000);

if (ie_opcao_p <> 'E') then
	begin
	
	open c02;
	loop
	fetch c02 into
		nm_usuario_exec_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		if (nm_usuario_exec_w IS NOT NULL AND nm_usuario_exec_w::text <> '') then
			ds_comunic_exec_w := ds_comunic_exec_w || Obter_Nome_Usuario(nm_usuario_exec_w) || chr(13)||chr(10);
		end if;
		end;
	end loop;
	close c02;
	
	if (ds_comunic_exec_w IS NOT NULL AND ds_comunic_exec_w::text <> '') then
		ds_comunicado_w := substr(ds_comunicado_w || chr(13)||chr(10) ||
			chr(13)||chr(10) || obter_desc_expressao(1075880) ||': '|| chr(13)||chr(10) || ds_comunic_exec_w,1,4000);
	end if;
	
	end;
end if;

open c01;
loop
fetch c01 into
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '') then
		begin
		select nextval('comunic_interna_seq')
		into STRICT nr_sequencia_w
		;

		insert into comunic_interna(
			dt_comunicado, ds_titulo, ds_comunicado, nm_usuario,
			dt_atualizacao, ie_geral, nm_usuario_destino, nr_sequencia,
			ie_gerencial, nr_seq_classif, dt_liberacao)
		values (
			clock_timestamp(), ds_titulo_w, ds_comunicado_w,
			nm_usuario_p, clock_timestamp(), 'N', nm_usuario_w || ',', nr_sequencia_w, 'N',
			nr_seq_classif_w, clock_timestamp());
	
		Insert into man_ordem_serv_envio(	nr_sequencia,
						nr_seq_ordem,
						dt_atualizacao,
						nm_usuario,
						dt_envio,
						ie_tipo_envio,
						ds_destino,
						ds_observacao)
					values (	nextval('man_ordem_serv_envio_seq'),
						nr_sequencia_p,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						'I',
						substr(nm_usuario_w,1,255),
						obter_desc_expressao(1075882));		
		commit;

		end;
	end if;
	end;

end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_comunic_alteracao_satisf (nr_sequencia_p bigint, nm_usuario_p text, ds_justificativa_p text, ie_opcao_p text) FROM PUBLIC;

